var popup=document.querySelector(".writeus"),popupOpener=document.querySelector("#writeus-opener"),popupClose=document.querySelector(".close-button"),input=document.querySelector(".writeus-input"),form=document.querySelector(".writeus-form"),nameSpace=document.querySelector("#name"),emailSpace=document.querySelector("#email"),letterSpace=document.querySelector("#letter");popupOpener.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("writeus-opened"),input.focus()}),popupClose.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("writeus-opened")}),form.addEventListener("submit",function(e){nameSpace.value&&emailSpace.value&&letterSpace.value||(e.preventDefault(),popup.classList.remove("writeus-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("writeus-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("writeus-opened")&&(e.preventDefault(),popup.classList.remove("writeus-opened"),popup.classList.remove("writeus-error"))});